class Solution {
    public boolean isConvex(int[][] points) {
        int n = points.length;
        if (n < 3) return false;
        int crossProductSign = 0;
        for (int i = 0; i < n; i++) {
            int p1x = points[i][0];
            int p1y = points[i][1];
            int p2x = points[(i + 1) % n][0];
            int p2y = points[(i + 1) % n][1];
            int p3x = points[(i + 2) % n][0];
            int p3y = points[(i + 2) % n][1];
            int crossProduct = (p2x - p1x) * (p3y - p2y) - (p2y - p1y) * (p3x - p2x);
            if (crossProduct != 0) {
                int currentSign = crossProduct > 0 ? 1 : -1;
                if (crossProductSign == 0) {
                    crossProductSign = currentSign;
                } else if (crossProductSign != currentSign) {
                    return false;
                }
            }
        }
        return true;
    }
}
