class Solution {
    public int kthSmallest(int[][] mat, int k) {
        PriorityQueue<Integer> pq = new PriorityQueue<>((a, b) -> b - a);
        pq.offer(0);
        for (int[] row : mat) {
            PriorityQueue<Integer> nextPq = new PriorityQueue<>((a, b) -> b - a);
            for (int prevSum : pq) {
                for (int num : row) {
                    nextPq.offer(prevSum + num);
                    if (nextPq.size() > k) {
                        nextPq.poll();
                    }
                }
            }
            pq = nextPq;
        }
        return pq.poll();
    }
}
