class Solution {
    public int maxIncreasingGroups(List<Integer> usageLimits) {
        Collections.sort(usageLimits);
        long sum = 0;
        int count = 0;
        for (int limit : usageLimits) {
            sum += limit;
            if (sum >= (long)(count + 1) * (count + 2) / 2) {
                count++;
            }
        }
        return count;
    }
}
