class Solution {
    public int[] numberOfPairs(int[] nums) {
        Map<Integer, Integer> count = new HashMap<>();
        for (int num : nums) {
            count.put(num, count.getOrDefault(num, 0) + 1);
        }
        int pairs = 0;
        int leftovers = 0;
        for (int c : count.values()) {
            pairs += c / 2;
            leftovers += c % 2;
        }
        return new int[]{pairs, leftovers};
    }
}
