class Solution {
    public int fillCups(int[] amount) {
        Arrays.sort(amount);
        int a = amount[2];
        int b = amount[1];
        int c = amount[0];
        int ans = 0;
        while (a > 0) {
            a--;
            b--;
            ans++;
            if (b < 0) break;
        }
        if (b > 0) ans += b;
        return ans;
    }
}
