class Solution {
    public int missingElement(int[] nums, int k) {
        int n = nums.length;
        int missing = 0;
        for (int i = 1; i < n; i++) {
            missing = nums[i] - nums[i - 1] - 1;
            if (k <= missing) {
                return nums[i - 1] + k;
            }
            k -= missing;
        }
        return nums[n - 1] + k;
    }
}
