class Solution {
    public int findProductPrice(int[][] prices, int timestamp) {
        TreeMap<Integer, Integer> priceMap = new TreeMap<>();
        for (int[] price : prices) {
            priceMap.put(price[0], price[1]);
        }
        Integer floorKey = priceMap.floorKey(timestamp);
        if (floorKey == null) {
            return -1;
        } else {
            return priceMap.get(floorKey);
        }
    }
    public int[] productPriceAtDate(int[][] prices, int[] timestamps) {
        TreeMap<Integer, Integer> priceMap = new TreeMap<>();
        for (int[] price : prices) {
            priceMap.put(price[0], price[1]);
        }
        int[] result = new int[timestamps.length];
        for (int i = 0; i < timestamps.length; i++) {
            Integer floorKey = priceMap.floorKey(timestamps[i]);
            if (floorKey == null) {
                result[i] = -1;
            } else {
                result[i] = priceMap.get(floorKey);
            }
        }
        return result;
    }
}
