class Solution {
    public int[] reportCounts(int[] reports) {
        int n = reports.length;
        int[] counts = new int[n];
        for (int i = 0; i < n; i++) {
            counts[i] = 0;
        }
        for (int i = 0; i < n; i++) {
            if (reports[i] >= 0) {
                counts[reports[i]]++;
            }
        }
        return counts;
    }
}
