class Solution {
    public char[][] rotateTheBox(char[][] box) {
        int m = box.length;
        int n = box[0].length;
        char[][] rotated = new char[n][m];
        for (int i = 0; i < n; i++) {
            Arrays.fill(rotated[i], '.');
        }
        for (int i = 0; i < m; i++) {
            int j = n - 1;
            for (int k = n - 1; k >= 0; k--) {
                if (box[i][k] == '#') {
                    rotated[k][m - 1 - i] = '#';
                    j--;
                } else if (box[i][k] == '*') {
                    rotated[k][m - 1 - i] = '*';
                    j = k - 1;
                }
            }
        }
        return rotated;
    }
}
