class Solution {
    public int minimumScore(String s, String t) {
        int n = s.length();
        int m = t.length();
        int i = 0, j = 0;
        int score = 0;
        while (i < n && j < m) {
            if (s.charAt(i) == t.charAt(j)) {
                j++;
            } else {
                score++;
            }
            i++;
        }
        score += m - j;
        return score;
    }
}
